record.field:
order_data = self.env['sale.order']._read_group(domain=[('x_source_sales_order', 'in', self.ids)], groupby=['x_source_sales_order'], aggregates=['__count'])
mapped_data = {order.id: count for order, count in order_data}
for order in self: order['x_x_source_sales_order_sale_order_count'] = mapped_data.get(order.id, 0)

groups = self.env['x_property']._read_group([('x_current_owner', 'in', self.ids)], ['x_current_owner'], ['__count'])
owner_property_counts = {owner.id: count for owner, count in groups}
for record in self: record['x_x_current_owner_x_property_count'] = owner_property_counts.get(record.id, 0)

groups = self.env['x_property']._read_group([('x_condominium', 'in', self.ids)], ['x_condominium'], ['__count'])
condo_counts = {condo.id: count for condo, count in groups}
for record in self: record['x_x_condominium_x_property_count'] = condo_counts.get(record.id, 0)

for record in self:
  voting_key = record.event_id.x_voting_key_id
  record['x_ratio'] = sum(voting_key.x_ratio_ids.filtered(lambda l: l.x_owner == record.partner_id).mapped('x_ratio')) if voting_key else 0.0

for record in self:
    if not record.x_vote_criteria or (record.x_in_favor == 0 and record.x_against == 0):
        record['x_outcome'] = False
    else:
        record['x_outcome'] = 'Approved' if record.x_in_favor >= record.x_vote_criteria.x_acceptation_threshold else 'Rejected'
